import React from 'react';
import styled, { css } from 'react-emotion';

import Hyperlink from '../Components/Hyperlink';
import Icon from '../Components/Icon';

import ReactMarkdown from 'react-markdown';

import PORTRAIT_IMAGE from '../../images/portrait.jpg';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  align-content: center;
  height: 100%;
  min-height: 100vh;
  text-align: center;
  padding: 10vw;
`;

const Portrait = styled.div`
  width: 21em;
  height: 21em;
  width: 40vh;
  height: 40vh;
  min-height: 10em;
  min-width: 10em;
  max-width: 60vw;
  max-height: 60vw;
  border-radius: 21em;
  margin-bottom: 2em;
  position: relative;

  ${({ useBorder }) =>
    useBorder &&
    `

    background: #000;
    border: 6px solid #000;
  `}
`;

const Image = styled.img`
  border-radius: inherit;
  object-fit: cover;
  object-position: center center;
  width: 100%;
  height: 100%;
  transition: transform 150ms ease-in;
  transform-origin: center center;
`;

const SVG = styled.svg`
  border-radius: inherit;
  object-fit: cover;
  object-position: center center;
  width: 100%;
  height: 100%;
  transition: transform 150ms ease-in;
  transform-origin: center center;

  path, g, linearGradient {
    will-change: fill;
    transition: all 0.5s ease-in;
  }

  ${({ indicateEasterEgg, showEasterEgg }) =>
    indicateEasterEgg && !showEasterEgg && css`
      &:hover {
        path[fill="#FFDFCC"],
        path[fill="#FCD4BD"] {
          fill: #ffc3b8;
        }

        path[fill="#FFDFCC"] {
          fill: #ffcdc4;
        }
      }
  `}

  ${({ showEasterEgg }) => showEasterEgg && css`

      linearGradient#b stop {
        stop-color: #a4f7fc;
      }

      path.eyes {
        fill: #ffcdcc;
      }

      path[fill="#FCD4BD"] {
        fill: #4286f4;
      }

      path[fill="#EDAE98"] {
        fill: #041735;
      }

      path[fill="#FFDFCC"] {
        fill: #3d7de2;
      }

      path[fill="#AF6868"] {
        fill: #3d7de2;
      }

      path[fill="#AF6868"] {
        fill: #0e1d75;
      }

      path[fill="#230C0C"] {
        fill: #020721;
      }

      path[fill="#935454"] {
        fill: #020721;
      }

      path[fill="#352315"] {
        fill: #071126;
      }

      path[fill="#BE8760"] {
        fill: #165b50;
      }

      g[fill="#CE9C7A"] {
        fill: #0da58d;
      }

      linearGradient {
        stop[stop-color="#D73723"] {
          stop-color: #02ffff;
        }

        stop[stop-color="#E37336"] {
          stop-color: #b92fef;
        }

        stop[stop-color="#EA9641"] {
          stop-color: #8f0bc4;
        }

        stop[stop-color="#FFE459"] {
          stop-color: #ef154c;
        }
      }
  `}
`;

Image.defaultProps = {
  alt: 'Yours Truly'
};

const CSS_STATES = [
  css`
    transform: scale3d(-1, 1, 1) rotate3d(0, 0, 1, 0deg);
  `,
  css`
    transform: scale3d(-1, 1, 1) rotate3d(0, 0, 1, 15deg);
  `,
  css`
    transform: scale3d(-1, 1, 1) rotate3d(0, 0, 1, 45deg);
  `,
  css`
    transform: scale3d(-1, 1, 1) rotate3d(0, 0, 1, 90deg);
  `,
  css`
    transform: scale3d(-1, 1, 1) rotate3d(0, 0, 1, 120deg);
  `,
  css`
    transform: scale3d(-1, 1, 1) rotate3d(0, 0, 1, 140deg);
  `,
  css`
    transform: scale3d(-1, 1, 1) rotate3d(0, 0, 1, 160deg);
  `
];

const Heading = styled.h1`
  margin: 0;
  font-size: 5em;
  font-size: calc(1em + 4vh);
  line-height: 1;
  letter-spacing: -0.04em;
  transition: color 150ms ease-in;

  ${({ isDarkMode }) => isDarkMode && css`color: #2ff3f9;`}
  ${({ showEasterEgg }) => showEasterEgg && css`
    color: red;
  `}
`;

const Subtitle = styled.h2`
  ${({ theme: { heading2 } }) => `${heading2}`} margin: 0;
  font-size: 1em;
  font-size: calc(1em + 1.3vh);
  @media (min-width: 500px) {
    font-size: calc(1em + 1.5vh);
  }
`;

const Menu = styled('nav')`
  font-size: 1.5em;
  margin-top: 1em;

  @media (min-width: 500px) {
    display: flex;
    flex-direction: row;
    justify-content: center;
    flex-wrap: wrap;
    flex: 0;
  }
`;

const Link = styled(Hyperlink)`
  display: block;
  margin: 0 1em;
  flex: 0 1 auto;
  min-width: 4em;
  text-align: center;
  color: #777;
  margin-bottom: 2em;

  &:hover {
    text-decoration: none;
    color: #000;
    position: relative;
    z-index: 5;

    @media screen and (prefers-color-scheme: dark) {
      color: #fff;
    }

    svg use {
      fill: currentColor;
    }
  }

  svg {
    display: block;
    font-size: 1.5em;
    margin: 0 auto 0;
    margin-bottom: 0.5rem;

    @media (max-width: 500px) {
      display: none;
    }
  }
`;

const BloodSplatterSVG = styled.svg`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  width: 100%;
  height: 100%;
  pointer-events: none;
  opacity: 0.45;
  object-fit: cover;
  object-position: center center;
  
  path {
    fill: red;
    z-index: -1;
    position: relative;
  }

  + svg {
    z-index: 3;
  }
`;

