const PortraitContainer = styled.div`
  width: 21em;
  height: 21em;
  width: 40vh;
  height: 40vh;
  min-height: 10em;
  min-width: 10em;
  max-width: 60vw;
  max-height: 60vw;
  border-radius: 21em;
  margin-bottom: 2em;
  position: relative;

  ${({ useBorder }) =>
    useBorder &&
    `

  background: #000;
  border: 6px solid #000;
`}
`;

const defaultEyesTransform = 'translate(299px, 392px) scaleY(1)';
const defaultEyesNoseBridgeTransform = 'translateY(0px) scaleY(1)';
const defaultEyeLidLinesTransform = 'translateY(0px) scaleY(1)';

const EYES_BLINK = keyframes`
  0%, 5%, 15%, 100% {
    transform: ${defaultEyesTransform};
  }

  10% {
    transform: translate(299px, 400px) scaleY(0.1);
  }
`;

const EYES_NOSE_BRIDGE_BLINK = keyframes`
  0%, 5%, 15%, 100% {
    transform: ${defaultEyesNoseBridgeTransform};
  }

  10% {
    transform: translateY(-3px) scaleY(0.95);
  }
`;

const EYE_LIDS_BLINK = keyframes`
  0%, 5%, 15%, 100% {
    transform:${defaultEyeLidLinesTransform};
  }

  10% {
    transform: translateY(10px) scaleX(1.1);
  }
`;

const SVGPortrait = styled.svg`
  border-radius: inherit;
  object-fit: cover;
  object-position: center center;
  width: 100%;
  height: 100%;
  transition: transform 150ms ease-in;
  transform-origin: center center;

  path,
  g,
  linearGradient {
    will-change: fill;
    transition: all 0.5s ease-in;
  }

  #Eye-Nose-Bridge,
  #Eyebrows,
  #Eye-Lid-Lines {
    transform-origin: center center;
  }

  #Eyes {
    transform: ${defaultEyesTransform};
  }

  #Eyes-Nose-Bridge {
    transform: ${defaultEyesNoseBridgeTransform};
  }

  #Eye-Lid-Lines {
    transform: ${defaultEyeLidLinesTransform};
  }

  ${({ indicateEasterEgg, showEasterEgg }) =>
    indicateEasterEgg &&
    !showEasterEgg &&
    css`
      &:hover {
        path[fill='#FFDFCC'],
        path[fill='#FCD4BD'] {
          fill: #ffc3b8;
        }

        path[fill='#FFDFCC'] {
          fill: #ffcdc4;
        }

        #Eye-Nose-Bridge,
        #Eyebrows {
          animation: ${EYES_NOSE_BRIDGE_BLINK} 3s ease-in 0s infinite backwards;
        }

        #Eye-Lid-Lines {
          animation: ${EYE_LIDS_BLINK} 3s ease-in 0s infinite backwards;
        }

        #Eyes {
          animation: ${EYES_BLINK} 3s ease-in 0s infinite backwards;
        }
      }
    `}

  ${({ showEasterEgg }) =>
    showEasterEgg &&
    css`
      linearGradient stop[stop-color='#273141'] {
        stop-color: #82dfe5;
      }

      linearGradient stop[stop-color='#466287'] {
        stop-color: #a4f7fc;
      }

      path.eyes {
        fill: #ffcdcc;
      }

      path[fill='#FCD4BD'] {
        fill: #4286f4;
      }

      path[fill='#EDAE98'] {
        fill: #041735;
      }

      path[fill='#FFDFCC'] {
        fill: #3d7de2;
      }

      path[fill='#AF6868'] {
        fill: #3d7de2;
      }

      path[fill='#AF6868'] {
        fill: #0e1d75;
      }

      path[fill='#230C0C'] {
        fill: #020721;
      }

      path[fill='#935454'] {
        fill: #020721;
      }

      path[fill='#352315'] {
        fill: #071126;
      }

      path[fill='#BE8760'] {
        fill: #165b50;
      }

      g[fill='#CE9C7A'] {
        fill: #0da58d;
      }

      linearGradient {
        stop[stop-color='#D73723'] {
          stop-color: #02ffff;
        }

        stop[stop-color='#E37336'] {
          stop-color: #b92fef;
        }

        stop[stop-color='#EA9641'] {
          stop-color: #8f0bc4;
        }

        stop[stop-color='#FFE459'] {
          stop-color: #ef154c;
        }
      }
    `}
`;

